import { useState } from "react";

function App() {
  const [product, setProduct] = useState({
    boxBarcode: "",
    productBarcode: "",
    name: "",
    spec: "",
    stock: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async () => {
    if (!product.productBarcode || !product.name) {
      alert("产品条码和名称不能为空");
      return;
    }
    try {
      const res = await fetch("http://localhost:3001/api/products", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(product),
      });
      if (res.ok) {
        alert("保存成功");
        setProduct({ boxBarcode: "", productBarcode: "", name: "", spec: "", stock: 0 });
      } else {
        alert("保存失败");
      }
    } catch (err) {
      alert("网络错误");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50 p-4">
      <div className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md">
        <h2 className="text-2xl font-bold mb-4 text-center">产品信息录入</h2>
        <div className="space-y-4">
          <input
            name="boxBarcode"
            placeholder="箱子条码（可选）"
            value={product.boxBarcode}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
          />
          <input
            name="productBarcode"
            placeholder="产品条码（必填）"
            value={product.productBarcode}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
          />
          <input
            name="name"
            placeholder="产品名称"
            value={product.name}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
          />
          <input
            name="spec"
            placeholder="规格（如 500g*12）"
            value={product.spec}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
          />
          <input
            name="stock"
            type="number"
            placeholder="库存数量"
            value={product.stock}
            onChange={handleChange}
            className="w-full border border-gray-300 p-2 rounded"
          />
          <button
            onClick={handleSubmit}
            className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700"
          >
            保存
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
